//本地存储
var lcstor = window.localStorage;

var $_ERROE_CODE='500';
var $_FAILD_CODE='-1';
var $_SUCCESS_CODE='0';
 
function setStorage(key, value){
    if (lcstor)
        lcstor[key] = value;
    //else
        //alert('localStorage error');
}
 
function getStorage(key){
    if (lcstor) {
        for (i in lcstor) {
            if (i == key)
                return lcstor[i];
        }
    }
    //else
        //alert('localStorage error');
}
 
function clearStorage(key){
    if (lcstor) {
        if (key)
            lcstor.removeItem(key);
        else
            lcstor.clear();
    }
    //else
        //alert('localStorage error');
}


//显示加载器  
function showLoader(msg) {  
    // 显示加载器.for jQuery Mobile 1.2.0
    $.mobile.loading('show', {  
        text: msg, // 加载器中显示的文字
        textVisible: true, // 是否显示文字
        theme: 'b',        // 加载器主题样式a-e
        textonly: true,   // 是否只显示文字
        html: ""           // 要显示的html内容，如图片等
    });
   setInterval(hideLoader, 4000);
}  

// 隐藏加载器.for jQuery Mobile 1.2.0
function hideLoader()  
{  
    // 隐藏加载器
    $.mobile.loading('hide');  
}  

