// 去除字符中的空格
function trim(str){  
        str = str.replace(/^(\s| )+/,'');  
        for(var i=str.length-1; i>=0; i--){  
        if(/\S/.test(str.charAt(i))){  
            str = str.substring(0, i+1);  
            break;  
        }  
        }  
        return str;  
    }  


Date.prototype.format =function(format)
{
    var o = {
    "M+" : this.getMonth()+1, // month
    "d+" : this.getDate(),    // day
    "h+" : this.getHours(),   // hour
    "m+" : this.getMinutes(), // minute
    "s+" : this.getSeconds(), // second
    "q+" : Math.floor((this.getMonth()+3)/3),  // quarter
    "S" : this.getMilliseconds() // millisecond
    }
    if(/(y+)/.test(format)) format=format.replace(RegExp.$1,
    (this.getFullYear()+"").substr(4- RegExp.$1.length));
    for(var k in o)if(new RegExp("("+ k +")").test(format))
    format = format.replace(RegExp.$1,
    RegExp.$1.length==1? o[k] :
    ("00"+ o[k]).substr((""+ o[k]).length));
    return format;
} 

function toDate(obj,format){
	 var date = new Date();
	 if(typeof(obj)!="undefined"){
	 date.setTime(obj.time);
	 date.setHours(obj.hours);
	 date.setMinutes(obj.minutes);
	 date.setSeconds(obj.seconds);
	 }
	 if(format == undefined || format == null){
		 format="yyyy-MM-dd HH:mm:ss";
	 }
	 return date.format(format); 
	}

/**
 * 数字加逗号
 * @param nStr
 * @returns
 */
function addCommas(nStr) {
	nStr += '';
	x = nStr.split('.');
	x1 = x[0];
	x2 = x.length > 1 ? '.' + x[1] : '';
	var rgx = /(\d+)(\d{3})/;
	while (rgx.test(x1)) {
		x1 = x1.replace(rgx, '$1' + ',' + '$2');
	}
	return x1 + x2;
}


String.prototype.lengthStr = function() {
	var len = 0;
	for ( var i = 0; i < this.length; i++) {
		if (this.charCodeAt(i) > 255 || this.charCodeAt(i) < 0) {
			len += 2
		} else {
			len++
		}
	}
	return len
};

String.prototype.strChars = function() {
	var chars = new Array();
	for ( var i = 0; i < this.length; i++) {
		chars[i] = [ this.substr(i, 1), this.isCHS(i) ]
	}
	String.prototype.charsArray = chars;
	return chars
};

String.prototype.isCHS = function(i) {
	if (this.charCodeAt(i) > 255 || this.charCodeAt(i) < 0) {
		return true
	} else {
		return false
	}
};

String.prototype.subString = function(start, end) {
	var len = 0;
	var str = "";
	this.strChars();
	for ( var i = 0; i < this.length; i++) {
		if (this.charsArray[i][1]) {
			len += 2
		} else {
			len++
		}
		;
		if (end < len) {
			return str
		} else if (start < len) {
			str += this.charsArray[i][0]
		}
	}
	;
	return str;
};

String.prototype.subChsString = function(start, length) {
	return this.subString(start, start + length)
};


/*延迟加载*/
function yanchi() {
	var animateDiv = $('.percent_small');
	//var animateDiv = $('.percent_small');
	animateEle();
	$(window).scroll(function() {
		animateEle()
	});
	function animateEle() {
		var playRange = {
			top : $(window).scrollTop(),
			bottom : $(window).scrollTop() + $(window).height()
		};
		animateDiv.each(function() {
			if (playRange.top <= $(this).offset().top
					&& playRange.bottom >= $(this).offset().top
					&& !$(this).data('bPlay')) {
				$(this).data('bPlay', true);
				var nPercent = $(this).parent().find('font').text()
						.replace(/\%/, '');
				//var nPercent = $(this).find('div').text().replace(/\%/, '');
				$(this).svgCircle({
					parent : $(this)[0],
					w : 40,
					R : 16,
					sW : 3,
					color : [ "#FE9D14", "#FE9D14", "#FE9D14" ],
					perent : [ 100, nPercent ],
					speed : 150,
					delay : 400
				});
			}
		});
	}
}

